mapper.select:
SELECT
    i.path avatarUrl
  FROM users i
  LEFT JOIN users_products j
  ON i.uid = j.uid
  LEFT JOIN products k
  ON j.pid = k.pid
  WHERE j.pid =#{id}
  AND j.uid=#{userId}
  AND j.isdz =1;

mapper.update:
UPDATE users_products i
SET i.isdz = i.isdz + 1
where i.pid =#{id}
AND i.uid=#{userId}

UPDATE users_products i
SET i.isdz = i.isdz - 1
where i.pid =#{id}
AND i.uid=#{userId}

mapper.select:
SELECT
    i.path,
    i.filename
  FROM products_files i
  WHERE i.pid = #{bridgeId1}
  AND i.classify = '0';

SELECT
    i.path,
    i.filename
  FROM products_files i
  WHERE i.pid = #{bridgeId2}
  AND i.classify = '1';

mapper.update:
UPDATE products i
SET i.browsenum = i.browsenum + 1
where i.pid =#{id}

UPDATE users_dynamic i
SET i.browsenum = i.browsenum + 1
where i.id =#{id}

UPDATE users_dynamic i
SET i.likenum = i.likenum + 1
where i.id =#{id}

UPDATE users_dynamic i
SET i.likenum = i.likenum - 1
where i.id =#{id}

UPDATE orders
SET turn_over = #{turnOver}
WHERE user_id = #{userId}
    AND id = #{id}

UPDATE orders t
SET t.status = #{status},
t.order_num = #{order_num}
WHERE t.id = #{id}
